#include "libftm_vec4.h"

float		ftm_vec4dot(t_ftmvec4 *a, t_ftmvec4 *b)
{
	return (a->v[0] * b->v[0] +
			a->v[1] * b->v[1] +
			a->v[2] * b->v[2] +
			a->v[3] * b->v[3]);
}
